declare global {
    var EdgeRuntime: unknown;
    var Netlify: unknown;
    var Bun: unknown;
    var Deno: unknown;
    var __lagon__: unknown;
    var fastly: unknown;
}
export type Runtime = 'edge-routine' | 'workerd' | 'deno' | 'lagon' | 'react-native' | 'netlify' | 'electron' | 'node' | 'bun' | 'edge-light' | 'fastly';
/**
 * Detect the current JavaScript runtime following
 * the WinterCG Runtime Keys proposal:
 *
 * - `edge-routine` - Alibaba Cloud Edge Routine
 * - `workerd` - Cloudflare Workers
 * - `deno` - Deno and Deno Deploy
 * - `lagon` - Lagon
 * - `react-native` - React Native
 * - `netlify` - Netlify Edge Functions
 * - `electron` - Electron
 * - `node` - Node.js
 * - `bun` - Bun
 * - `edge-light` - Vercel Edge Functions
 * - `fastly` - Fastly Compute@Edge
 *
 * @see https://runtime-keys.proposal.wintercg.org/
 * @returns {Runtime}
 */
export declare const detectRuntime: () => Runtime | undefined;
